input.css:
@charset 'utf-8';
@import 'settings.css';
@custom-selector :--heading h1,h2,h3,h4;

ul $size {
  padding: $size;
}
ul li a .something {
  color: white;
}
@define-mixin icon $color {
  padding-left: 16px;
  color: blue;
  background: $color;
}
button {
  @mixin icons twitter gray;
  border-radius: 5px;
  padding: 5px 10px;
  box-shadow: 0 0 3px red, 0 0 10px green, inset 0 0 3px blue;
}

